import { usePostDetails } from "../hooks/use-post-details";

function Details() {
  const { loading, error, records } = usePostDetails();

  return (
    <div>
      <p>ID: {records.id}</p>
      <p>Title: {records.title}</p>
      <p>Description: {records.description}</p>
    </div>
  );
}

export default Details;
